async () => {
		await Modify_pipeline2.run()
		for (let i = 0;i<appsmith.store.class_id.length;i++){
			await Insert_class1.run({org: appsmith.store.class_id[i]})
		}
		await ListAPI.run()
		if (ListAPI.data.filter(item=>item.name==="apollo_"+Table1.selectedRow.pipeline_id)[0].status==="running")
		{
			StopAPI.run(()=> RemoveAPI.run(()=> ModifyAPI.run))
		}
    else
			{
				RemoveAPI.run(()=> ModifyAPI.run)
			}

	await Select.run().then(()=>showAlert("Pipeline ID "+appsmith.store.pipeline_id+" modified successfully"))
		closeModal('Modal2')
	}

